import React, { Component,useState, setState } from 'react';
import PropTypes from "prop-types";
import "./mynode.css";
import { Modal, Button, Form , Row, Col, Card, ListGroup } from "react-bootstrap";
// import "bootstrap/dist/css/bootstrap.css";
import styled from "styled-components";
import { Link } from 'react-router-dom';
import CardHeader from 'react-bootstrap/esm/CardHeader';
import { person } from "../assets";


const DropDownContainerNew = styled("div")`
  width: 200px;
  margin: 0 auto;

`;

const DropDownContainerEdit = styled("div")`
  width: 200px;
  margin: 0 auto;

`;

const DropDownHeaderNew = styled("div")`
  margin-bottom: 0.8em;
  padding: 0.4em 2em 0.4em 1em;
  box-shadow: 0 2px 3px rgba(0, 0, 0, 0.15);
  font-weight: 500;
  font-size: 15px;
  color: #ffffff;
  background: #6593F5;
  border-radius:5px;
`;

const DropDownHeaderEdit = styled("div")`
  margin-bottom: 0.8em;
  padding: 0.4em 2em 0.4em 1em;
  box-shadow: 0 2px 3px rgba(0, 0, 0, 0.15);
  font-weight: 500;
  font-size: 15px;
  color: #6593F5;
  background: #ffffff;
  border-radius:5px;
  border-color: #6593f5;
  border-width: 2px;
  border-style: solid;

`;

const DropDownListContainerNew = styled("div")``;
const DropDownListContainerEdit = styled("div")``;

const DropDownListNew = styled("ul")`
  padding:0;
  margin-bottom: 40px;
  background: #ffffff;
  border: 2px solid #e5e5e5;
  box-sizing: border-box;
  color: #646464;
  font-size: 0px;
  font-weight: 400;
  &:first-child {
    padding-top: 0.8em;
  }
  border-radius:5px;
`;

const DropDownListEdit = styled("ul")`
 padding:0;
  margin-bottom: 40px;
  background: #ffffff;
  border: 2px solid #e5e5e5;
  box-sizing: border-box;
  color: #646464;
  font-size: 0px;
  font-weight: 400;
  &:first-child {
    padding-top: 0.8em;
  }
  border-radius:5px;
`;

const ListItemNew = styled("li")`
  list-style: none;
  padding:6px;
  margin-bottom:5px;
  font-size: 15px;
`;

const ListItemEdit = styled("li")`
  list-style: none;
  padding:6px;
  margin-bottom:5px;
  font-size: 15px;
`;

const propTypes = {
  // nodeData: PropTypes.object.isRequired
  nodeData: PropTypes.oneOfType([PropTypes.object]).isRequired
};



const MyNode = ({ nodeData }) => {
  const [isOpenNew, setIsOpenNew] = useState(false);
  const togglingNew = () => setIsOpenNew(!isOpenNew);

  const [isOpenEdit, setIsOpenEdit] = useState(false);
  const togglingEdit = () => setIsOpenEdit(!isOpenEdit);



 const [show,setShow] =useState(false);

 const CompClose = () => setShow(false);
 const CompShow = () => setShow(true);


  return (
     <div >

        <Card bg="warning" text="white" style={{ width: '18rem' }} className="mb-2" onClick={ CompShow }>
        <Card.Img variant="top" src="holder.js/100px180" />
          <Card.Body>
           <Card.Title>{nodeData.title_goals}</Card.Title>
            <Card.Text>
            {nodeData.desc_goals}
            </Card.Text>
          </Card.Body>
          <Card.Footer>2 days ago</Card.Footer>
        </Card>


      <Modal className="" show={ show } onHide={ CompClose }>
        <Modal.Header closeButton>
            <Modal.Title><div className="cta-2 text-primary">{nodeData.title_goals}</div></Modal.Title>
        </Modal.Header>
        <Modal.Body className="Model">
           {/* <div> 
              <button type="button" className="closeButton" onClick={CompClose}>x</button>
           </div> */}

            <div className="modelBody"> 
              <form className=''>
                <fieldset disabled>
                    <div className='mb-3 form-floating'>
                      <input disabled className='form-control' value={nodeData.desc_goals} />
                      <label className='form-label'>desc goals</label>
                    </div>
                </fieldset>
                <fieldset disabled>
                    <div className='mb-3 form-floating'>
                      <input disabled className='form-control' value={nodeData.pic_goals} />
                      <label className='form-label'>PIC</label>
                    </div>
                </fieldset>
                <fieldset disabled className='row'>
                    <div className='mb-3 form-floating col-sm-6'>
                      <input disabled className='form-control' value={nodeData.start_date} />
                      <label className='form-label'>Start Date</label>
                    </div>
                    <div className='mb-3 form-floating col-sm-6'>
                      <input disabled className='form-control' value={nodeData.due_date} />
                      <label className='form-label'>Due Date</label>
                    </div>
                </fieldset>
                <fieldset disabled>
                <div className='mb-3 form-floating'>
                      <input disabled className='form-control' value={nodeData.last_modified_date} />
                      <label className='form-label'>Last Modify Date</label>
                    </div>
                </fieldset>
                <fieldset disabled>
                    <div className='mb-3 form-floating'>
                      <input disabled className='form-control' value={nodeData.progress} />
                      <label className='form-label'>Progress</label>
                    </div>
                </fieldset>
                <fieldset disabled>
                    <div className='mb-3 form-floating'>
                      <input disabled className='form-control' value={nodeData.type_goals} />
                      <label className='form-label'>Indikator</label>
                    </div>
                </fieldset>
              </form>

            {/* <DropDownContainerNew>
              <DropDownHeaderNew onClick={togglingNew}>ADD NEW</DropDownHeaderNew>
              {isOpenNew && (
                <DropDownListContainerNew>
                  <DropDownListNew className="DropDownListNew">
                    <a style={{ textDecoration: 'none', color:'gray' }} href="/AddValue"> <ListItemEdit>Perspective</ListItemEdit></a>   
                    <a style={{ textDecoration: 'none', color:'gray' }} href="/AddValue"> <ListItemEdit>Goal</ListItemEdit></a>   
                    <a style={{ textDecoration: 'none', color:'gray' }} href="/AddValue"> <ListItemEdit>Strategy</ListItemEdit></a>   
                    <a style={{ textDecoration: 'none', color:'gray' }} href="/AddValue"> <ListItemEdit>Action Plan</ListItemEdit></a>   
                    <a style={{ textDecoration: 'none', color:'gray' }} href="/AddValue"> <ListItemEdit>Task</ListItemEdit></a>   

                     </DropDownListNew>
              </DropDownListContainerNew>
              )}
            </DropDownContainerNew>

            <DropDownContainerEdit>
              <DropDownHeaderEdit onClick={togglingEdit}>EDIT</DropDownHeaderEdit>
              {isOpenEdit && (
                <DropDownListContainerEdit >
                  <DropDownListEdit className="DropDownListEdit">
                    <a style={{ textDecoration: 'none', color:'gray' }} href="/EditValue"> <ListItemEdit>Goal 1</ListItemEdit></a>   
                    <a style={{ textDecoration: 'none', color:'gray' }} href="/EditValue"> <ListItemEdit>Goal 2</ListItemEdit></a>   
                    <a style={{ textDecoration: 'none', color:'gray' }} href="/EditValue"> <ListItemEdit>Perspective 1</ListItemEdit></a>   
                    <a style={{ textDecoration: 'none', color:'gray' }} href="/EditValue"> <ListItemEdit>Perspective 2</ListItemEdit></a>   
                    <a style={{ textDecoration: 'none', color:'gray' }} href="/EditValue"> <ListItemEdit>Strategy 1</ListItemEdit></a>   
                </DropDownListEdit>
              </DropDownListContainerEdit>
              )}
            </DropDownContainerEdit> */}


            </div>
        </Modal.Body>

      </Modal>

    </div>

  );
};


MyNode.propTypes = propTypes;

export default MyNode;
