import React, { useEffect, useState } from "react";
import SortableTree from "react-sortable-tree";
import { Row, Col, Card } from 'react-bootstrap';
import HtmlHead from 'components/html-head/HtmlHead';
import BreadcrumbList from 'components/breadcrumb-list/BreadcrumbList';
import { toast } from 'react-toastify';
import { useSelector } from 'react-redux';
import { TreeView } from '../services/treeservice';

const TreeAdminFun = () => {
    const { currentUser, isLogin } = useSelector((state) => state.auth);
    const [treeData, getGoals] = useState('');
    const [isLoading, setLoading] = useState(true);
    const [nodeClicked, clickNode] = useState(true);

    const getAllGoals = () => {

      let result = null;
      const Signups  =  TreeView(currentUser.token).then(function(response) {
        if(response) {
          if(response.responseCode === 200) {
            toast.success(response.responseDesc, {
              position: "top-right",
              autoClose: 5000,
              hideProgressBar: false,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: true,
              progress: undefined,
            });
            // console.log(response.responseData);
            result = response.responseData;
            console.log(result);
            getGoals(result);
            setLoading(false);
          }else{  
            toast.error(response.responseDesc, {
              position: "top-right",
              autoClose: 5000,
              hideProgressBar: false,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: true,
              progress: undefined,
            });
          }
        }
      });
    }

    const handleNodeClick = (node) => {
        // setState({
        //     // nodeClicked: node

        // });
        clickNode(node);
    };

    const handleTreeOnChange = (treeDataS) => {
        // setState({ treeData });
        getGoals(treeDataS);
    };

    const alertNodeInfo = ({ node, path, treeIndex }) => {
        console.log(node);
        console.log(path);
        console.log(treeIndex);
        const objectString = Object.keys(node)
          .map((k) => (k === "children" ? "children: Array" : `${k}: '${node[k]}'`))
          .join(",\n   ");

        alert(
          "Info passed to the button generator:\n\n" +
            `node: {\n   ${objectString}\n},\n` +
            `path: [${path.join(", ")}],\n` +
            `treeIndex: ${treeIndex}`
        );
    };

    const loopParseData = (data,parent) => {
        let resJson = "";
        if(data) {
            data.forEach(
                function(d,idx,arr) {
                    if(d.expanded) {
                        // this.loopParseData(d);
                        console.log(`${d.title}, id : ${d.id}, parent : ${parent} Parent of : `);
                        resJson += `${d.title} Parent of : `;
                        loopParseData(d.children,d.id);
                    }else{
                        resJson += `${d.title}, id : ${d.id}, parent : ${parent}`;
                        console.log(`${d.title}, id : ${d.id}, parent : ${parent}`);
                    }
                }.bind(this)
            );
        }
    }

    const handleClickButton = () => {
        // const { treeData } = this.state;
        // const loopData = '';
        // console.log(treeData.keys("children"));
        // treeData.forEach(
        //     function(d) {
        //         console.log(d);
        //         if(d.expanded) {

        //         }
        //     }
        // );
        loopParseData(treeData,0);
    }


    useEffect(() => {
        getAllGoals();
    }, []);

    const maxDepth = 3; 
    const title = 'Tree Admin Page';
    const description = 'An page for configure the tree view.';
    const breadcrumbs = [{ to: '', text: 'Home' }];

    if (isLoading) {
        return <div className="App">Loading...</div>;
    }

    return (
        <div className="App" style={{ height: 500 }}>
            <HtmlHead title={title} description={description} />
            <Row>
                <Col>
                    {/* Title Start */}
                    <section className="scroll-section" id="title">
                    <div className="page-title-container">
                        <h1 className="mb-0 pb-0 display-4">{title}</h1>
                        <BreadcrumbList items={breadcrumbs} />
                    </div>
                    <Card className="mb-5" body>
                        <Card.Text>{description}</Card.Text>
                        <SortableTree
                            treeData={treeData}
                            onChange={handleTreeOnChange}
                            isVirtualized= {false}
                            maxDepth={maxDepth}
                            generateNodeProps={(rowInfo) => {
                                const { node } = rowInfo;
                                return {
                                    buttons: [
                                    <button
                                        type="button"
                                        key={node}
                                        className="btn-xs btn-outline-success"
                                        style={{
                                        verticalAlign: "middle"
                                        }}
                                        onClick={() => alertNodeInfo(rowInfo)}
                                    >
                                        ℹ
                                    </button>
                                    ],
                                    onClick: () => {
                                        handleNodeClick(node);
                                    },
                                    style:
                                    node === nodeClicked
                                        ? {
                                            border: "3px solid yellow"
                                        }
                                        : {}
                                };
                            }}
                            />
                            <button type="button" onClick={handleClickButton}>
                                Click Me
                            </button>
                    </Card>
                    </section>
                {/* Title End */}
                </Col>
            </Row>

        </div>
    )

};

export default TreeAdminFun;
