import { DEFAULT_PATHS } from 'config.js';

import HomePage from 'views/default/Home';
import HorizontalPage from 'views/Horizontal';
import VerticalPage from 'views/Vertical';
import TreePage from 'views/TreeAdmin';
import TreeView from 'views/TreeView';
import TreeViewDabeng from 'views/TreeViewDabeng';
import TreeViewMap from 'views/TreeViewMap';
import { USER_ROLE } from 'constants.js';

const appRoot = DEFAULT_PATHS.APP.endsWith('/') ? DEFAULT_PATHS.APP.slice(1, DEFAULT_PATHS.APP.length) : DEFAULT_PATHS.APP;

const routesAndMenuItems = {
  mainMenuItems: [
    {
      path: DEFAULT_PATHS.APP,
      exact: true,
      redirect: true,
      to: `${appRoot}/home`,
      roles: [USER_ROLE.Admin,USER_ROLE.Editor],
    },
    {
      path: `${appRoot}/home`,
      component: HomePage,
      label: 'Home',
      icon: 'grid-2',
      roles: [USER_ROLE.Admin,USER_ROLE.Editor],
    },
    // {
    //   path: `${appRoot}/horizontal`,
    //   component: HorizontalPage,
    //   label: 'menu.horizontal',
    //   icon: 'grid-2',
    //   roles: [USER_ROLE.Admin],
    // },
    // {
    //   path: `${appRoot}/vertical`,
    //   label: 'menu.vertical',
    //   icon: 'grid-3',
    //   component: VerticalPage,
    //   roles: [USER_ROLE.Admin],
    // },
    {
      path: `${appRoot}/tree`,
      label: 'Tree',
      icon: 'grid-3',
      subs:[{
        path: `${appRoot}/treeadm`,
        label: 'Tree Admin',
        icon: 'grid-2',
        component: TreePage,
        roles: [USER_ROLE.Admin],
      },
      // {
      //   path: `${appRoot}/treeview`,
      //   label: 'Tree View',
      //   icon: 'grid-2',
      //   component: TreeView,
      //   roles: [USER_ROLE.Admin,USER_ROLE.Editor],
      // },
      {
        path: `${appRoot}/treeviewd`,
        label: 'Tree View D',
        icon: 'grid-2',
        component: TreeViewDabeng,
        roles: [USER_ROLE.Admin,USER_ROLE.Editor],
      },
      {
        path: `${appRoot}/treeviewm`,
        label: 'Tree View M',
        icon: 'grid-2',
        component: TreeViewMap,
        roles: [USER_ROLE.Admin,USER_ROLE.Editor],
      }]
    },
  ],
  sidebarItems: [],
};
export default routesAndMenuItems;
