import React, { useEffect, useState } from "react";
import { useHistory } from 'react-router-dom';
import { Button, Row, Col, Card, Nav, Form, Dropdown } from 'react-bootstrap';
import DatePicker from 'react-datepicker';
import Select from 'react-select';
import { useSelector } from 'react-redux';
import * as Yup from 'yup';
import { useFormik } from 'formik';
import HtmlHead from 'components/html-head/HtmlHead';
import BreadcrumbList from 'components/breadcrumb-list/BreadcrumbList';
import CsLineIcons from 'cs-line-icons/CsLineIcons';
import useCustomLayout from 'hooks/useCustomLayout';
import 'react-datepicker/dist/react-datepicker.css';
import { useWindowSize } from 'hooks/useWindowSize';
import { toast } from 'react-toastify';
import { AddChildTreeService } from '../services/treeservice';
import { DEFAULT_PATHS } from '../config';
import { LAYOUT } from '../constants';

const UpdNode = (props) => {
    const appRoot = DEFAULT_PATHS.APP.endsWith('/') ? DEFAULT_PATHS.APP.slice(1, DEFAULT_PATHS.APP.length) : DEFAULT_PATHS.APP;
    const { currentUser, isLogin } = useSelector((state) => state.auth);
    const history = useHistory();
    const states = props;
    const parent = states.location.state;
    // console.log(parent);
    // console.log(parent.title);
    const [startDate, setStartDate] = useState(new Date());
    const [dueDate, setDueDate] = useState(new Date());
    useCustomLayout({ layout: LAYOUT.Boxed });
    const { width } = useWindowSize();

    const { themeValues } = useSelector((state) => state.settings);
    const lgBreakpoint = parseInt(themeValues.lg.replace('px', ''), 10);

    const title = 'Add Child Page';
    const description = 'An page for adding child the tree view node.';
    const breadcrumbs = [
        { to: ``, text: 'Home' },
        { to: `tree/treeadmf`, text: 'Tree Admin' },
    ];

    const handleClickBackButton = () => {
        const path = `${appRoot}/tree/treeadmf`; 
        // console.log(path);
        history.push(path);
    };

    const onSubmit = (values) => {

        // console.log('submit form', values);
        const signin  =  AddChildTreeService(currentUser.token, values.childTitle, values.childDesc, currentUser.email, startDate, dueDate, parent.id).then(function(response) {
        //   console.log(response);
          if(response) {
            if(response.responseCode === 200) {
              toast.success(response.responseDesc, {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
              });
            //   const path = `home`; 
            //   history.push(path);
            }else{  
              toast.error(response.responseDesc, {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
              });
            }
          }
        });   
    };

    const validationSchema = Yup.object().shape({
        childTitle: Yup.string().required('Title is required'),
        childDesc: Yup.string().required('Description is required'),
        // startDate: Yup.string().required('Start date is required'),
        // dueDate: Yup.string().required('Due date is required'),
    });

    const initialValues = { childTitle: '', childDesc: '', startDate: '', dueDate: ''};

    const formik = useFormik({ initialValues, validationSchema, onSubmit });
    const { handleSubmit, handleChange, values, touched, errors } = formik;

    return (
        <div className="App" style={{ height: 500 }}>
            <HtmlHead title={title} description={description} />
            <Row>
                <Col>
                    {/* Title Start */}
                    <section className="scroll-section" id="title">
                    <div className="page-title-container">
                        <h1 className="mb-0 pb-0 display-4">{title}</h1>
                        <BreadcrumbList items={breadcrumbs} />
                    </div>
                    </section>
                </Col>
            </Row>
            <Row>
                <Col>
                    <Form id="loginForm" className="tooltip-end-bottom" onSubmit={handleSubmit}>
                    <h2 className="small-title">Child Info</h2>
                    <Card className="mb-2">
                        <Card.Body className="p-3">
                                <Row className="mb-2 filled tooltip-end-top">
                                    <Col lg="2" md="3" sm="4">
                                        <Form.Label className="col-form-label">Title Child</Form.Label>
                                    </Col>
                                    <Col sm="8" md="9" lg="10">
                                        <Form.Control type="text" name="childTitle" id="childTitle" values={values.childTitle}  onChange={handleChange}/>
                                        {errors.childTitle  && touched.childTitle && <div className="d-block invalid-tooltip">{errors.childTitle}</div>}
                                    </Col>
                                </Row>
                                <Row className="mb-2 filled tooltip-end-top">
                                    <Col lg="2" md="3" sm="4">
                                        <Form.Label className="col-form-label">Description Child</Form.Label>
                                    </Col>
                                    <Col sm="8" md="9" lg="10">
                                        <Form.Control type="text" name="childDesc" id="childDesc" values={values.childDesc}  onChange={handleChange}/>
                                        {errors.childDesc  && touched.childDesc && <div className="d-block invalid-tooltip">{errors.childDesc}</div>}
                                    </Col>
                                </Row>
                                <Row className="mb-2 filled tooltip-end-top">
                                    <Col lg="2" md="2" sm="2">
                                        <Form.Label className="col-form-label">Start Date</Form.Label>
                                    </Col>
                                    <Col sm="4" md="4" lg="4">
                                        {/* <Form.Control type="text" name="startDate" id="startDate" defaultValue=""/> */}
                                        <DatePicker className="form-control" name="startDates" id="startDates" values={startDate} selected={startDate} onChange={(date) => setStartDate(date)} />
                                        {errors.startDate  && touched.startDate && <div className="d-block invalid-tooltip">{errors.startDate}</div>}
                                    </Col>
                                    <Col lg="2" md="2" sm="2">
                                        <Form.Label className="col-form-label">Due Date</Form.Label>
                                    </Col>
                                    <Col sm="4" md="4" lg="4" >
                                        {/* <Form.Control type="text" name="dueDate" id="dueDate" defaultValue=""/> */}
                                        <DatePicker className="form-control" name="dueDates" id="dueDates" values={dueDate} selected={dueDate} onChange={(date) => setDueDate(date)} />
                                        {errors.dueDate  && touched.dueDate && <div className="d-block invalid-tooltip">{errors.dueDate}</div>}
                                    </Col>
                                </Row>
                                <Row className="mt-5">
                                    <Col lg="2" md="3" sm="4" />
                                    <Col sm="8" md="9" lg="10">
                                        <div className="btn-group">
                                            <Button type="submit" variant="outline-primary" className="mb-1">Submit</Button>
                                            <Button id="backButton" name="backButton" type="button" variant="outline-warning" className="mb-1" onClick={handleClickBackButton}>Back</Button>
                                        </div>
                                    </Col>
                                </Row>
                        </Card.Body>
                    </Card>
                    </Form>
                </Col>
            </Row>
        </div>
    );
}

export default UpdNode;
