import {React,useState,} from 'react';
import { Row, Col, Card, Form , Button} from 'react-bootstrap';
import * as Yup from 'yup';
import { useFormik } from 'formik';
import HtmlHead from 'components/html-head/HtmlHead';
import BreadcrumbList from 'components/breadcrumb-list/BreadcrumbList';
import useCustomLayout from 'hooks/useCustomLayout';
import { MENU_PLACEMENT, LAYOUT } from 'constants.js';
import CsLineIcons from 'cs-line-icons/CsLineIcons';
import { useSelector } from 'react-redux';
import { toast } from 'react-toastify';
import { GetAllUsers } from '../services/userservice';

const RowInd = function(propss){
    const {firstname, role, onClick} = propss;

    // console.log(props);
    return (
        <Row className="g-0 sh-13 sh-md-6 mb-3">
          <Col xs="auto">
            <img src="/img/profile/profile-1.webp" className="card-img rounded-xl sh-6 sw-6" alt="thumb" />
          </Col>
          <Col>
            <Card.Body className="pt-0 pb-0 pe-0 h-100">
              <Row className="g-0 h-100 align-items-start align-content-start align-content-md-center align-items-md-center">
                <Col md="5" className="d-flex align-items-center mb-md-0">
                  {/* Winry Rockbell */}
                  {firstname}
                </Col>
                <Col md="4" className="d-flex align-items-center text-muted pe-2 mb-2 mb-md-0">
                  {/* Project Manager */}
                  {role}
                </Col>
                <Col md="3" className="d-flex align-items-center justify-content-md-end">
                    <Button size="sm" variant="outline-muted" className="btn-icon btn-icon-only hover-outline me-1">
                        <CsLineIcons icon="facebook" onClick={onClick}/>
                    </Button>
                </Col>
              </Row>
            </Card.Body>
          </Col>
        </Row>
    );
}

const UserSettingPage = () => {
  const title = 'User Setting Page';
  const description = 'List of Users.';
  const { currentUser, isLogin } = useSelector((state) => state.auth);

  const rowsId = [];
  const [rowState, setRowState] = useState(rowsId);
  const [ds, getUsers] = useState('');
  const [isLoading, setLoading] = useState(true);

  const breadcrumbs = [{ to: '', text: 'Home' }];

  const onSubmit = (values) => {
    // console.log(values);
    // const chart = document.getElementsByClassName('myChart');
    // // chart[0].classLiss;
    // // console.log(chart[0].getAttribute('class'));
    // chart[0].setAttribute('class',`${chart[0].getAttribute('class')} noncollapsable`);
    // // console.log(chart[0]);
    // chart[0].querySelectorAll(["classname=node"]).forEach();
  }

  const getAllUsers = () => {

    setLoading(true);
    let result = null;
    const Signups  =  GetAllUsers(currentUser.token).then(function(response) {
      if(response) {
        // console.log(response);
        if(response.responseCode === 200) {
          toast.success(response.responseDesc, {
            position: "top-right",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });
          // console.log(response.responseData);
          result = response.responseData;
          // console.log(result.type_goals);
          getUsers(result);
          setLoading(false);
        }else{  
          toast.error(response.responseDesc, {
            position: "top-right",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });

          // if(response.responseCode === 401) {
          //   // dispatch(setCurrentUser(''));
          //   const path = `${appRoot}/login`; 
          //   history.push(path);
          // }
        }
      }
    });
  };

  useEffect(() => {
    GetAllUsers();
  }, []);

  const clickRows = () => {

  };

  const initialValues = { searchField: '' };
  const validationSchema = Yup.object().shape({
    searchField: Yup.string().required('Search is required'),
  });
  const formik = useFormik({ initialValues, validationSchema, onSubmit });
  const { handleSubmit, handleChange, values, touched, errors } = formik;

  useCustomLayout({ placement: MENU_PLACEMENT.Vertical, layout: LAYOUT.Boxed });

  return (
    <>
      <HtmlHead title={title} description={description} />
      <Row>
        <Col>
          {/* Title Start */}
          <section className="scroll-section" id="title">
            <div className="page-title-container">
              <h1 className="mb-0 pb-0 display-4">{title}</h1>
              <BreadcrumbList items={breadcrumbs} />
            </div>
            <Row>
                <Col>
                <Card className="mb-5" body>
                    <div className="g-0 row">
                        <div className="d-flex align-items-start justify-content-end justify-content-lg-start col-md col-12">
                            <form id="searchForm" className="tooltip-end-bottom me-lg-auto w-md-auto search-input-container border border-separator " onSubmit={handleSubmit}>
                            <div className="input-group">
                            <Form.Control id="searchField" className="" placeholder="Search" value={values.searchField} onChange={handleChange} />
                            <button id="button-addon" type="submit" className="btn btn-outline-secondary">
                                <span className="search-magnifier-icon pe-none">
                                    <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 20 20" fill="none" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round" className="cs-icon search ">
                                    <circle cx="9" cy="9" r="7"> </circle>
                                    <path d="M14 14L17.5 17.5"> </path>
                                    </svg>
                                </span>
                            </button>
                            </div>
                            </form> 
                        </div>
                    </div> 
                </Card>
            </Col>
            </Row>
            <Card className="mb-5" body>
                <Card.Text>{description}</Card.Text>
                <div className="display-data-Container">
                    {ds.map((row, idx) => {
                        return(
                            <RowInd 
                            key={idx}
                            firstname={row.name}
                            role={row.role}
                            onClick={(e) => clickRows(row)}
                            /> 
                        )
                    })
                }
                </div>
            </Card>
          </section>
          {/* Title End */}
        </Col>
      </Row>
    </>
  );
};

export default UserSettingPage;
