import React, { useEffect, useState } from "react";
import { useHistory } from 'react-router-dom';
import { Button, Row, Col, Card, Nav, Form, Dropdown } from 'react-bootstrap';
import DatePicker from 'react-datepicker';
import Select from 'react-select';
import { useSelector } from 'react-redux';
import * as Yup from 'yup';
import { useFormik } from 'formik';
import HtmlHead from 'components/html-head/HtmlHead';
import BreadcrumbList from 'components/breadcrumb-list/BreadcrumbList';
import CsLineIcons from 'cs-line-icons/CsLineIcons';
import useCustomLayout from 'hooks/useCustomLayout';
import 'react-datepicker/dist/react-datepicker.css';
import { useWindowSize } from 'hooks/useWindowSize';
import { toast } from 'react-toastify';
import { AddChildTreeService } from '../services/treeservice';
import { DEFAULT_PATHS } from '../config';
import { LAYOUT } from '../constants';

const NavContent = () => {
    return (
      <Nav className="flex-column">
        <div className="mb-2">
          <Nav.Link className="px-0 active">
            <CsLineIcons icon="activity" className="me-2 sw-3" size="17" />
            <span className="align-middle">Profile</span>
          </Nav.Link>
          <div>
            <Nav.Link className="px-0 pt-1 active">
              <i className="me-2 sw-3 d-inline-block" />
              <span className="align-middle">Personal</span>
            </Nav.Link>
            <Nav.Link className="px-0 pt-1">
              <i className="me-2 sw-3 d-inline-block" />
              <span className="align-middle">Friends</span>
            </Nav.Link>
            <Nav.Link className="px-0 pt-1">
              <i className="me-2 sw-3 d-inline-block" />
              <span className="align-middle">Account</span>
            </Nav.Link>
          </div>
        </div>
        <div className="mb-2">
          <Nav.Link className="px-0">
            <CsLineIcons icon="credit-card" className="me-2 sw-3" size="17" />
            <span className="align-middle">Payment</span>
          </Nav.Link>
          <div>
            <Nav.Link className="px-0 pt-1">
              <i className="me-2 sw-3 d-inline-block" />
              <span className="align-middle">Billing</span>
            </Nav.Link>
            <Nav.Link className="px-0 pt-1">
              <i className="me-2 sw-3 d-inline-block" />
              <span className="align-middle">Invoice</span>
            </Nav.Link>
            <Nav.Link className="px-0 pt-1">
              <i className="me-2 sw-3 d-inline-block" />
              <span className="align-middle">Tax Info</span>
            </Nav.Link>
          </div>
        </div>
        <div className="mb-2">
          <Nav.Link className="px-0">
            <CsLineIcons icon="shield" className="me-2 sw-3" size="17" />
            <span className="align-middle">Security</span>
          </Nav.Link>
          <div>
            <Nav.Link className="px-0 pt-1">
              <i className="me-2 sw-3 d-inline-block" />
              <span className="align-middle">Password</span>
            </Nav.Link>
            <Nav.Link className="px-0 pt-1">
              <i className="me-2 sw-3 d-inline-block" />
              <span className="align-middle">Security Log</span>
            </Nav.Link>
            <Nav.Link className="px-0 pt-1">
              <i className="me-2 sw-3 d-inline-block" />
              <span className="align-middle">Devices</span>
            </Nav.Link>
          </div>
        </div>
        <div className="mb-2">
          <Nav.Link className="px-0">
            <CsLineIcons icon="notification" className="me-2 sw-3" size="17" />
            <span className="align-middle">Notifications</span>
          </Nav.Link>
        </div>
        <div className="mb-2">
          <Nav.Link className="px-0">
            <CsLineIcons icon="tablet" className="me-2 sw-3" size="17" />
            <span className="align-middle">Applications</span>
          </Nav.Link>
        </div>
      </Nav>
    );
};

const AddChildTree = (props) => {
    const appRoot = DEFAULT_PATHS.APP.endsWith('/') ? DEFAULT_PATHS.APP.slice(1, DEFAULT_PATHS.APP.length) : DEFAULT_PATHS.APP;
    const { currentUser, isLogin } = useSelector((state) => state.auth);
    const history = useHistory();
    const states = props;
    const parent = states.location.state;
    console.log(parent);
    // console.log(parent.title);
    const [startDate, setStartDate] = useState(new Date());
    const [dueDate, setDueDate] = useState(new Date());
    useCustomLayout({ layout: LAYOUT.Boxed });
    const { width } = useWindowSize();

    const { themeValues } = useSelector((state) => state.settings);
    const lgBreakpoint = parseInt(themeValues.lg.replace('px', ''), 10);

    const title = 'Add Child Page';
    const description = 'An page for adding child the tree view node.';
    const breadcrumbs = [
        { to: ``, text: 'Home' },
        { to: `tree/treeadmf`, text: 'Tree Admin' },
    ];

    const handleClickBackButton = () => {
        const path = `${appRoot}/tree/treeadmf`; 
        // console.log(path);
        history.push(path);
    };

    const validationSchema = Yup.object().shape({
        childTitle: Yup.string().required('Title is required'),
        childDesc: Yup.string().required('Description is required'),
        startDates: Yup.string().required('Start date is required'),
        dueDates: Yup.string().required('Due date is required'),
    });

    const initialValues = { childTitle: '', childDesc: '', startDate: '', dueDate: ''};

    const onSubmit = (values) => {

        // console.log('submit form', values);
        const signin  =  AddChildTreeService(currentUser.token, values.childTitle, values.childDesc, currentUser.name, values.startDate, values.dueDate, parent.id).then(function(response) {
        //   console.log(response);
          if(response) {
            if(response.responseCode === 200) {
              toast.success(response.responseDesc, {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
              });
            //   const path = `home`; 
            //   history.push(path);
            }else{  
              toast.error(response.responseDesc, {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
              });
            }
          }
        });   
    };

    const formik = useFormik({ initialValues, validationSchema, onSubmit });
    const { handleSubmit, handleChange, values, touched, errors } = formik;

    return (
        <div className="App" style={{ height: 500 }}>
            <HtmlHead title={title} description={description} />
            <Row>
                <Col>
                    {/* Title Start */}
                    <section className="scroll-section" id="title">
                    <div className="page-title-container">
                        <h1 className="mb-0 pb-0 display-4">{title}</h1>
                        <BreadcrumbList items={breadcrumbs} />
                    </div>
                    </section>
                </Col>
                {/* Top Buttons Start */}
                {width && width < lgBreakpoint && (
                    <Col xs="12" sm="auto" className="d-flex align-items-start justify-content-end d-block d-lg-none">
                    <Dropdown align="end">
                        <Dropdown.Toggle as={Button} variant="outline-primary" className="btn-icon btn-icon-start btn-icon w-100 w-sm-auto">
                        <CsLineIcons icon="gear" /> <span>Settings </span>
                        </Dropdown.Toggle>
                        <Dropdown.Menu className="sw-25 py-3 px-4">
                        <NavContent />
                        </Dropdown.Menu>
                    </Dropdown>
                    </Col>
                )}
            </Row>
            <Row>
                <Col>
                    <h2 className="small-title">Parent Info</h2>

                    <Form id="loginForm" className="tooltip-end-bottom" onSubmit={handleSubmit}>
                    <Card className="mb-3">
                        <Card.Body className="p-3">
                                <Row>
                                    <Col lg="2" md="3" sm="4">
                                        <Form.Label className="col-form-label">Title Parent</Form.Label>
                                    </Col>
                                    <Col sm="8" md="9" lg="10">
                                        <Form.Label type="text" className="col-form-label" text={parent.title} name="parentTitle" id="parentTitle" value={parent.title} defaultValue={parent.title} readOnly={1}>{parent.title}</Form.Label>
                                        {errors.parentTitle  && touched.parentTitle && <div className="d-block invalid-tooltip">{errors.parentTitle}</div>}
                                        <Form.Label type="text" name="parentId" id="parentId" defaultValue={parent.id} value={parent.id} readOnly={1} hidden={1}/>
                                    </Col>
                                </Row>
                        </Card.Body>
                    </Card>
                    <h2 className="small-title">Child Info</h2>
                    <Card className="mb-2">
                        <Card.Body className="p-3">
                                <Row className="mb-2">
                                    <Col lg="2" md="3" sm="4">
                                        <Form.Label className="col-form-label">Title Child</Form.Label>
                                    </Col>
                                    <Col sm="8" md="9" lg="10">
                                        <Form.Control type="text" name="childTitle" id="childTitle" values={values.childTitle} />
                                        {errors.childTitle  && touched.childTitle && <div className="d-block invalid-tooltip">{errors.childTitle}</div>}
                                    </Col>
                                </Row>
                                <Row className="mb-2">
                                    <Col lg="2" md="3" sm="4">
                                        <Form.Label className="col-form-label">Description Child</Form.Label>
                                    </Col>
                                    <Col sm="8" md="9" lg="10">
                                        <Form.Control type="text" name="childDesc" id="childDesc" values={values.childDesc}/>
                                        {errors.childDesc  && touched.childDesc && <div className="d-block invalid-tooltip">{errors.childDesc}</div>}
                                    </Col>
                                </Row>
                                <Row className="mb-2">
                                    <Col lg="2" md="2" sm="2">
                                        <Form.Label className="col-form-label">Start Date</Form.Label>
                                    </Col>
                                    <Col sm="4" md="4" lg="4">
                                        {/* <Form.Control type="text" name="startDate" id="startDate" defaultValue=""/> */}
                                        <DatePicker className="form-control" name="startDates" id="startDates" selected={startDate} onChange={(date) => setStartDate(date)} />
                                        {errors.startDates  && touched.startDates && <div className="d-block invalid-tooltip">{errors.startDates}</div>}
                                    </Col>
                                    <Col lg="2" md="2" sm="2">
                                        <Form.Label className="col-form-label">Due Date</Form.Label>
                                    </Col>
                                    <Col sm="4" md="4" lg="4">
                                        {/* <Form.Control type="text" name="dueDate" id="dueDate" defaultValue=""/> */}
                                        <DatePicker className="form-control" name="dueDates" id="dueDates" selected={dueDate} onChange={(date) => setDueDate(date)} />
                                        {errors.dueDates  && touched.dueDates && <div className="d-block invalid-tooltip">{errors.dueDates}</div>}
                                    </Col>
                                </Row>
                                <Row className="mt-5">
                                    <Col lg="2" md="3" sm="4" />
                                    <Col sm="8" md="9" lg="10">
                                        <div className="btn-group">
                                            <Button type="submit" variant="outline-primary" className="mb-1">Submit</Button>
                                            <Button id="backButton" name="backButton" type="button" variant="outline-warning" className="mb-1" onClick={handleClickBackButton}>Back</Button>
                                        </div>
                                    </Col>
                                </Row>
                        </Card.Body>
                    </Card>
                    </Form>
                </Col>
            </Row>
        </div>
    );
}

export default AddChildTree;
